import React from 'react';
import { useNavigate } from 'react-router-dom'; 
import './MainPage.css';// Import CSS for styling

const M = () => {
    const navigate = useNavigate(); // Initialize navigate function
    
    const handleRoleClick = (role) => {
        if (role === 'Register') {
            navigate(`/registerpage`);  // Navigate directly to the Register page
        } else {``
            navigate(`/login/${role.toLowerCase()}`);
 // Navigate to the respective login page based on the role
        }
    };

    return (
        <div className="signup-container">
            <div className="button-container">
                <button className="btn" onClick={() => handleRoleClick('Register')}>Register</button>
                <button className="btn" onClick={() => handleRoleClick('Administrator')}>Administrator Login</button>
                <button className="btn" onClick={() => handleRoleClick('Trainer')}>Trainer Login</button>
                <button className="btn" onClick={() => handleRoleClick('Employee')}>Employee Login</button>
            </div>
        </div>
    );
};

export default Signup;
