// src/pages/RequestLearningPlan.js
import React, { useState } from 'react';

function RequestLearningPlan() {
  const [technology, setTechnology] = useState('');
  const [areasOfImprovement, setAreasOfImprovement] = useState('');
  const [learningGoals, setLearningGoals] = useState('');

  const handleRequest = () => {
    alert(`Requested learning plan for: ${technology}`);
  };

  return (
    <div>
      <h2>Request Learning Plan</h2>
      <select onChange={(e) => setTechnology(e.target.value)}>
        <option value="">Select Technology</option>
        <option value="React">React</option>
        <option value="Node.js">Node.js</option>
      </select>
      <textarea
        placeholder="Areas of Improvement"
        value={areasOfImprovement}
        onChange={(e) => setAreasOfImprovement(e.target.value)}
      />
      <textarea
        placeholder="Learning Goals"
        value={learningGoals}
        onChange={(e) => setLearningGoals(e.target.value)}
      />
      <button onClick={handleRequest}>Submit</button>
    </div>
  );
}

export default RequestLearningPlan;
