import React, { useState, useEffect } from 'react';

function UserManagement() {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [editUser, setEditUser] = useState(null); // For handling user editing
  const [newUserName, setNewUserName] = useState(''); // For editing user name
  const [userRole, setUserRole] = useState(''); // For managing user role changes

  useEffect(() => {
    async function fetchUsers() {
      try {
        const response = await fetch('http://localhost:5000/users');
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    }

    fetchUsers();
  }, []);

  // Filter users based on search term
  const filteredUsers = users.filter(user =>
    user.name && user.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Handle user deletion
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/users/${id}`, { method: 'DELETE' });
      setUsers(users.filter(user => user._id !== id)); // Update local state
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  // Handle user editing
  const handleEdit = (user) => {
    setEditUser(user);
    setNewUserName(user.name); // Pre-fill the input with the current name
    setUserRole(user.role); // Pre-fill the role
  };

  const handleSaveEdit = async () => {
    try {
      const response = await fetch(`http://localhost:5000/users/${editUser._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: newUserName, role: userRole }), // Send the updated role as well
      });

      if (response.ok) {
        const updatedUser = await response.json();
        // Update users in the local state
        setUsers(users.map(user => (user._id === updatedUser._id ? updatedUser : user)));
        setEditUser(null); // Reset editing state
        setNewUserName(''); // Clear input
        setUserRole(''); // Clear role
      } else {
        console.error('Failed to update user:', response.statusText);
      }
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div>
      <h2 className="centered-heading">User Management</h2>
      <input
        type="text"
        placeholder="Search users..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />
      <button className="add-user-btn">Add User</button>

      {/* User List */}
      <table className="user-list">
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length > 0 ? (
            filteredUsers.map(user => (
              <tr key={user._id}>
                <td>
                  {editUser && editUser._id === user._id ? (
                    <input
                      type="text"
                      value={newUserName}
                      onChange={(e) => setNewUserName(e.target.value)} // Update name
                    />
                  ) : (
                    user.name
                  )}
                </td>
                <td>
                  {editUser && editUser._id === user._id ? (
                    <select 
                      value={userRole}
                      onChange={(e) => setUserRole(e.target.value)} // Update role
                      className="role-dropdown"
                    >
                      <option value="Admin">Admin</option>
                      <option value="User">User</option>
                      <option value="Trainer">Trainer</option>
                    </select>
                  ) : (
                    user.role
                  )}
                </td>
                <td>
                  {editUser && editUser._id === user._id ? (
                    <button onClick={handleSaveEdit} className="save-btn">Save</button> // Save button
                  ) : (
                    <button onClick={() => handleEdit(user)} className="edit-btn">Edit</button> // Edit button
                  )}
                  <button onClick={() => handleDelete(user._id)} className="delete-btn">Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">No users found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default UserManagement;
