import React, { useState, useEffect } from 'react';

function UserManagement() {
  const [users, setUsers] = useState([]);  // Initially, an empty list of users
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch users from backend
  useEffect(() => {
    async function fetchUsers() {
      try {
        const response = await fetch('http://localhost:5000/user'); // Assuming your backend runs on port 5000
        const data = await response.json();
        setUsers(data);  // Update state with fetched users
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    }
    
    fetchUsers();
  }, []);  // Empty dependency array means this runs once when the component mounts

  // Filter users based on search term
  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2 className="centered-heading">User Management</h2>
      <p>Manage user accounts and roles here.</p>

      {/* Search and Filter options */}
      <input
        type="text"
        placeholder="Search users..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />

      {/* Add User Button */}
      <button className="add-user-btn">Add User</button>

      {/* User List */}
      <table className="user-list">
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length > 0 ? (
            filteredUsers.map(user => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>
                  {/* Role assignment dropdown */}
                  <select value={user.role} className="role-dropdown">
                    <option value="Admin">Admin</option>
                    <option value="User">User</option>
                    <option value="Trainer">Trainer</option>
                  </select>
                </td>
                <td>
                  {/* Edit and Delete buttons */}
                  <button className="edit-btn">Edit</button>
                  <button className="delete-btn">Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">No users found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default UserManagement;
