// src/pages/RequestQuestionBank.js
import React, { useState } from 'react';

function RequestQuestionBank() {
  const [technology, setTechnology] = useState('');

  const handleRequest = () => {
    // Handle request logic
    alert(`Requested question bank for: ${technology}`)
  };

  return (
    <div>
      <h2>Request Question Bank</h2>
      <select onChange={(e) => setTechnology(e.target.value)}>
        <option value="">Select Technology</option>
        <option value="React">React</option>
        <option value="Node.js">Node.js</option>
        <option value="Python">Python</option>
      </select>
      <button onClick={handleRequest}>Request</button>
    </div>
  );
}

export default RequestQuestionBank;
