import React, { useState } from 'react';

function UserManagement() {
  // Sample user data (can be replaced with real data from an API or database)
  const [users, setUsers] = useState([
    { id: 1, name: 'John Doe', role: 'Admin' },
    { id: 2, name: 'Jane Smith', role: 'User' },
    { id: 3, name: 'Bob Johnson', role: 'Trainer' }
  ]);
  
  const [searchTerm, setSearchTerm] = useState('');

  // Filter users based on search term
  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2 className="centered-heading">User Management</h2>
      <p>Manage user accounts and roles here.</p>

      {/* Search and Filter options */}
      <input
        type="text"
        placeholder="Search users..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />

      {/* Add User Button */}
      <button className="add-user-btn">Add User</button>

      {/* User List */}
      <table className="user-list">
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map(user => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>
                {/* Role assignment dropdown */}
                <select value={user.role} className="role-dropdown">
                  <option value="Admin">Admin</option>
                  <option value="User">User</option>
                  <option value="Trainer">Trainer</option>
                </select>
              </td>
              <td>
                {/* Edit and Delete buttons */}
                <button className="edit-btn">Edit</button>
                <button className="delete-btn">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserManagement;
