import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import UserManagement from './pages/UserManagement';
import SystemMonitoring from './pages/SystemMonitoring';
import ReportGeneration from './pages/ReportGeneration';
import Settings from './pages/Settings';
import IssueResolution from './pages/IssueResolution';
import Logout from './pages/Logout';
import DashBoard from './pages/Dashboard'; // Ensure this path is correct

function Admin() {
  return (
    <Router>
      <div className="dashboard-container">
        {/* Sidebar */}
        <nav className="dashboard-sidebar">
          <ul>
            <li><Link to="/admin">Dashboard</Link></li>
            <li><Link to="/admin/user-management">User Management</Link></li>
            <li><Link to="/admin/system-monitoring">System Monitoring</Link></li>
            <li><Link to="/admin/report-generation">Report Generation</Link></li>
            <li><Link to="/admin/settings">Settings</Link></li>
            <li><Link to="/admin/issue-resolution">Issue Resolution</Link></li>
            <li><Link to="/admin/logout">Logout</Link></li>
          </ul>
        </nav>

        {/* Main Content */}
        <main className="dashboard-content">
          <Routes>
            <Route path="/admin" element={<DashBoard />} /> {/* Corrected the syntax for component rendering */}
            <Route path="/admin/user-management" element={<UserManagement />} />
            <Route path="/admin/system-monitoring" element={<SystemMonitoring />} />
            <Route path="/admin/report-generation" element={<ReportGeneration />} />
            <Route path="/admin/settings" element={<Settings />} />
            <Route path="/admin/issue-resolution" element={<IssueResolution />} />
            <Route path="/admin/logout" element={<Logout />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default Ap;
