// src/pages/RequestQuestionBank.js
import React, { useState } from 'react';
import './Employee.css'; // Import your CSS file for styles

const RequestQuestionBank = () => {
  const [technology, setTechnology] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle question bank request logic here
    alert(`Requesting question bank for: ${technology}`);
  };

  return (
    <div>
      <h2 className="centered-heading">Request Question Bank</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Select Technology:
          <select value={technology} onChange={(e) => setTechnology(e.target.value)}>
            <option value="">Select...</option>
            <option value="Tech1">Technology 1</option>
            <option value="Tech2">Technology 2</option>
          </select>
        </label>
        <br />
        <button type="submit">Request</button>
      </form>
    </div>
  );
};

export default RequestQuestionBank;
